import React, { useState } from 'react';
import { Form, Modal, DatePicker, Button, Image } from 'antd';
import { ImageUpload, NgTable } from 'src/components';
import { PlusOutlined } from '@ant-design/icons';
import ChooseMoment from 'src/pages/LiveCode/MomentCode/components/AccessChatModal/ChooseMoment';
import style from './style.module.less';

interface IAccessChatProps {
  value?: any[];
  onChange?: (value?: any) => void;
}

const AccessChat: React.FC<IAccessChatProps> = ({ value, onChange }) => {
  const [accessChatModalVisible, setAccessChatModalVisivle] = useState(false);

  const { Item } = Form;
  const [form] = Form.useForm();

  const onCancelHandle = () => {
    setAccessChatModalVisivle(false);
  };

  const onOkHandle = () => {
    onChange?.(undefined);
  };

  return (
    <>
      <Button className={style.addMoment} icon={<PlusOutlined />} onClick={() => setAccessChatModalVisivle(true)}>
        接入群聊
      </Button>
      <div className={style.tips}>入群人数超过200人将自动切换到下一个群</div>
      <NgTable
        className={style.table}
        scroll={{ x: 550 }}
        dataSource={
          value || [
            {
              momentName: '群号码1',
              timeEnd: '2022-05-01'
            }
          ]
        }
        columns={[
          {
            title: '群二维码',
            render () {
              // return <img width={48} height={48} src={require('src/assets/images/SaleClue/momentId.jpg')} />;
              return (
                <Image
                  width={48}
                  height={48}
                  src={''}
                  fallback="data:image/png;base64,"
                />
              );
            }
          },
          { title: '群活码名称', dataIndex: 'momentName' },
          { title: '群二维码有效期', dataIndex: 'timeEnd' },
          {
            title: '操作',
            render () {
              return <span style={{ color: '#318cf5' }}>编辑</span>;
            }
          }
        ]}
      />
      <Modal
        title="接入企业微信群聊"
        visible={accessChatModalVisible}
        className={style.modalWrap}
        onCancel={onCancelHandle}
        centered
        maskClosable={false}
        onOk={onOkHandle}
        width={620}
      >
        <Form form={form}>
          <Item label="企业微信群" required extra="员工或管理员可在企业微信后台或移动客户端">
            <ChooseMoment />
          </Item>
          <Item
            label="群二维码"
            name="chatCode"
            required
            extra="群二维码需和客户群对应；支持jpg/png格式图片，且大小不超过2M"
          >
            <ImageUpload />
          </Item>
          <Item label="群码有效期" required extra="群二维码有效期为7天，请选择实际客户群二维码的有效日期">
            <DatePicker className={style.datePicker} />
          </Item>
        </Form>
      </Modal>
    </>
  );
};
export default AccessChat;
